drop table if exists pms_product;
create table pms_product
(
    id                            bigint auto_increment
        primary key,
    brand_id                      bigint                             not null comment '品牌id',
    product_category_id           bigint                             not null comment '商品分类id',
    feight_template_id            bigint                             not null comment '商品运费模板id',
    product_attribute_category_id bigint                             not null comment '商品属性分类id',
    name                          varchar(64)                        not null comment '商品名称',
    pic                           varchar(255)                       not null comment '商品图片',
    product_sn                    varchar(64)                        not null comment '货号',
    delete_status                 tinyint                            not null comment '删除状态：0->未删除；1->已删除',
    publish_status                tinyint                            not null comment '上架状态：0->下架；1->上架',
    new_status                    tinyint                            not null comment '新品状态:0->不是新品；1->新品',
    recommand_status              tinyint                            not null comment '推荐状态；0->不推荐；1->推荐',
    verify_status                 tinyint                            not null comment '审核状态：0->未审核；1->审核通过',
    sort                          int                                not null comment '排序',
    sale                          int                                not null comment '销量',
    price                         bigint                             not null comment '商品价格',
    promotion_price               bigint                             not null comment '促销价格',
    gift_growth                   int      default 0                 not null comment '赠送的成长值',
    gift_point                    int      default 0                 not null comment '赠送的积分',
    use_point_limit               int                                not null comment '限制使用的积分数',
    sub_title                     varchar(255)                       not null comment '副标题',
    description                   text                               not null comment '商品描述',
    original_price                bigint                             not null comment '市场价',
    stock                         int                                not null comment '库存',
    low_stock                     int                                not null comment '库存预警值',
    unit                          varchar(16)                        not null comment '单位',
    weight                        bigint                             not null comment '商品重量，默认为克',
    preview_status                tinyint                            not null comment '是否为预告商品：0->不是；1->是',
    service_ids                   varchar(64)                        not null comment '以逗号分割的产品服务：1->无忧退货；2->快速退款；3->免费包邮',
    keywords                      varchar(255)                       not null comment '搜索关键字',
    note                          varchar(255)                       not null comment '备注',
    album_pics                    varchar(255)                       not null comment '画册图片，连产品图片限制为5张，以逗号分割',
    detail_title                  varchar(255)                       not null comment '详情标题',
    detail_desc                   text                               not null comment '详情描述',
    detail_html                   text                               not null comment '产品详情网页内容',
    detail_mobile_html            text                               not null comment '移动端网页详情',
    promotion_start_time          datetime                           null comment '促销开始时间',
    promotion_end_time            datetime                           null comment '促销结束时间',
    promotion_per_limit           int      default 0                 not null comment '活动限购数量',
    promotion_type                tinyint                            not null comment '促销类型：0->没有促销使用原价;1->使用促销价；2->使用会员价；3->使用阶梯价格；4->使用满减价格；5->限时购',
    brand_name                    varchar(255)                       not null comment '品牌名称',
    product_category_name         varchar(255)                       not null comment '商品分类名称',
    product_category_id_Array     varchar(64)                        not null comment '商品分类id字符串',
    create_time                   datetime default CURRENT_TIMESTAMP not null comment '创建时间',
    update_time                   datetime                           null on update CURRENT_TIMESTAMP comment '更新时间'
)
    comment '商品信息';


