drop table if exists sys_menu;
create table sys_menu
(
    id             bigint auto_increment comment '菜单id'
        primary key,
    menu_name           varchar(50)                         not null comment '菜单名称',
    parent_id      bigint                              not null comment '父菜单ID，一级菜单为0',
    menu_path            varchar(200)                        default ''                not null comment '前端路由',
    menu_perms          varchar(500)                        default ''                not null comment '权限标识',
    menu_type           int                                 not null comment '类型 0：目录,1：菜单,2：按钮,3：外链',
    menu_icon           varchar(50)                         default ''                not null comment '菜单图标',
    menu_sort   tinyint      default 0                 not null comment '菜单排序',
    create_by      varchar(50)                         not null comment '创建者',
    create_time    timestamp default CURRENT_TIMESTAMP not null comment '创建时间',
    update_by      varchar(50)                         default ''                not null comment '更新者',
    update_time datetime   null on update CURRENT_TIMESTAMP comment '更新时间',
    menu_status tinyint default 1                              not null comment '菜单状态',
    is_deleted    tinyint      default 1                 not null comment '是否删除  0：否  1：是',
    is_visible    tinyint      default 1                 not null comment '是否可见  0：否  1：是',
    remark      varchar(255) default ''                not null comment '备注信息',
    vue_path       varchar(64)                         default ''                not null comment 'vue系统的path',
    vue_component  varchar(64)                         default ''                not null comment 'vue的页面',
    vue_icon       varchar(64)                         default ''                not null comment 'vue的图标',
    vue_redirect   varchar(64)                         default ''                not null comment 'vue的路由重定向',
    background_url varchar(256)                        default ''                not null comment '接口地址'
)
    comment '菜单信息表';


